else if(column_name.equalsIgnoreCase("DataSet")) { int column_Num = scenariotestDataAccess.columnIndex("Req_Parameter", column_name); if(counter>9) { utility.setValue(workbook,worksheet,counter,column_Num,"DS"+counter); } else { utility.setValue(workbook,worksheet,counter,column_Num,"DS0"+counter); } } else { int column_Num = scenariotestDataAccess.columnIndex("Req_Parameter", column_name);; try { utility.setValue(workbook,worksheet,counter,column_Num,request_record.getString(column_name)); log.info "RQ Get Value: "+request_record.getString(column_name); } catch(Exception ex) { log.info "Col not exists in DB: "+column_name; if(!utility.getCellType(workbook,worksheet,counter,column_Num).equalsIgnoreCase("Blank")) { utility.setValue(workbook,worksheet,counter,column_Num,scenariotestDataAccess.getValue(counter,column_Num)); } else { utility.setValue(workbook,worksheet,counter,column_Num,"<BLANK>"); System.out.println("BLANK"); } } } } counter++; }
fsIP.close(); FileOutputStream output_file =new FileOutputStream(new File(scenario_Path)); workbook.write(output_file); output_file.close(); } } else { log.info "RQ Record Set Not Found"; }
