/*JAVA CLASSES*/
/*********************************************************************************/
/*********************************************************************************/
public class DBUtilities { public Connection getDBConnection(String driver, String server, String schema, String username, String password) {
Connection connMySQL = null; try { Class.forName(driver); connMySQL = DriverManager.getConnection(server + schema , username , password); } catch (Exception ex) { e.printStackTrace()
} return connMySQL;
}
public ResultSet getMySQLData(Connection con, String SQLQRY) { ResultSet RS = null; try { Statement stmnt = con.createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_UPDATABLE);
RS = stmnt.executeQuery(SQLQRY); } catch (Exception E) { e.printStackTrace(); }
return RS;
}
public void setValue(HSSFWorkbook workbook, HSSFSheet sheet, int rowNum, int columnNum, String cellValue) { try { HSSFCell cell = sheet.getRow(rowNum).getCell(columnNum); cell.setCellValue(cellValue); } catch(Exception e) { HSSFRow new_row = sheet.createRow(rowNum); HSSFCell cell = new_row.createCell(columnNum); cell.setCellType(Cell.CELL_TYPE_STRING); cell.setCellValue(cellValue); setCellStyle(workbook, cell); } }
private String getCellType(HSSFWorkbook workbook, HSSFSheet sheet, int rowNum, int columnNum) { String cell_type = null; try { HSSFCell cell = sheet.getRow(rowNum).getCell(columnNum); if (cell.getCellType() == Cell.CELL_TYPE_NUMERIC || cell.getCellType() == Cell.CELL_TYPE_STRING || cell.getCellType() == Cell.CELL_TYPE_BOOLEAN) { cell_type = "Filled"; } else if (cell.getCellType() == Cell.CELL_TYPE_BLANK) { cell_type = "Blank"; } } catch(Exception e) { HSSFRow new_row = sheet.createRow(rowNum); HSSFCell cell = new_row.createCell(columnNum); cell.setCellValue("<BLANK>"); cell.setCellType(Cell.CELL_TYPE_STRING); setCellStyle(workbook, cell); } return cell_type; }
private void setCellStyle(HSSFWorkbook workbook, HSSFCell cell) { HSSFCellStyle cellStyle = workbook.createCellStyle(); cellStyle = workbook.createCellStyle(); HSSFFont hSSFFont = workbook.createFont(); hSSFFont.setFontName(HSSFFont.FONT_ARIAL); hSSFFont.setFontHeightInPoints((short) 11); cellStyle.setFont(hSSFFont); cellStyle.setBorderTop(HSSFCellStyle.BORDER_MEDIUM); cellStyle.setBorderBottom(HSSFCellStyle.BORDER_MEDIUM); cellStyle.setBorderLeft(HSSFCellStyle.BORDER_MEDIUM); cellStyle.setBorderRight(HSSFCellStyle.BORDER_MEDIUM); cell.setCellStyle(cellStyle); }
public List<ResultSet> getRQParamList(String scenrio_Path, String sheet) { List<ResultSet> request_params = new ArrayList<ResultSet>(); DBUtilities utility = new DBUtilities(); ExcelDataAccess scenariotestDataAccess = new ExcelDataAccess(scenrio_Path); scenariotestDataAccess.setDatasheetName(sheet); ResultSet objRecordSet = scenariotestDataAccess.getScenarioExcelData(); try { while (objRecordSet.next()) { String driver = objRecordSet.getString("Driver"); String db_server = objRecordSet.getString("DBServer"); String schema = objRecordSet.getString("Schema"); String username = objRecordSet.getString("Username"); String password = objRecordSet.getString("Password"); String sql_Query = objRecordSet.getString("SQL_Query"); String input_sheet = objRecordSet.getString("Input_Sheet"); Connection connect = utility.getDBConnection(driver,db_server,schema,username,password); if(input_sheet.contains("Req_Parameter")) { ResultSet resultSet = utility.getMySQLData(connect,sql_Query); request_params.add(resultSet); } } } catch (SQLException e) { // TODO Auto-generated catch block e.printStackTrace(); } return request_params; }
