public String getConfig(String Key) { String Value = null; DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance(); DocumentBuilder builder = factory.newDocumentBuilder(); Document document = builder.parse("Y:\\Groups\\Web_Next_Gen\\AUTOMATION\\PUBLIC\\AUTOMATIC_FRAMEWORK_EXECUTION\\DBSettings.xml"); document.getDocumentElement().normalize(); // log.info "Root element :" + document.getDocumentElement().getNodeName() NodeList nList = document.getElementsByTagName("Variable"); for (int temp = 0; temp < nList.getLength(); temp++) { Node nNode = nList.item(temp); //log.info nNode.getNodeName() if (nNode.getNodeType() == Node.ELEMENT_NODE) { Element eElement = (Element) nNode;
//log.info eElement.getElementsByTagName("Name").item(0).getTextContent() if (eElement.getElementsByTagName("Name").item(0).getTextContent() == Key) { Value = eElement.getElementsByTagName("Value").item(0).getTextContent() log.info eElement.getElementsByTagName("Value").item(0).getTextContent() } }
} return Value; }
public String getXMlNodevalue(String node)
{
String globalFile = context.expand('${projectDir}') + "/GlobalSettings.xml"
String nodevalue = null;
try {
// Setup the parser
DocumentBuilderFactory builderFactory = DocumentBuilderFactory.newInstance();
DocumentBuilder builder = builderFactory.newDocumentBuilder();
// Read the XML file
File inputFile = new File(globalFile);
InputStream inputStream = new FileInputStream(inputFile);
// Parse the XML file
Document doc = builder.parse(inputStream);
// Create an XPath instance
XPath xPath = XPathFactory.newInstance().newXPath();
// Evaluate simple expressions
String expression = "//Variable/" + node;
nodevalue = xPath.evaluate(expression, doc);
log.info "<<< Using XPath to read node values >>>"
log.info nodevalue
return nodevalue;
} catch (FileNotFoundException e) {
e.printStackTrace();
} catch (XPathExpressionException e) {
e.printStackTrace();
} catch (SAXException e) {
e.printStackTrace();
} catch (IOException e) {
e.printStackTrace();
} catch (ParserConfigurationException e) {
e.printStackTrace();
}
}
public String getSettingsProperty(String propKey) { Properties properties = new Properties(); String strValue = "";
try { properties.load(new FileInputStream(context.expand('${projectDir}') + "/Global_Settings.properties"));
strValue = properties.getProperty(propKey); } catch (FileNotFoundException e) { e.printStackTrace(); } catch (IOException e) { e.printStackTrace(); } return strValue; }
