public ResultSet getMySQLData(Connection con, String SQLQRY) { ResultSet RS = null; try { Statement stmnt = con.createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_UPDATABLE);
RS = stmnt.executeQuery(SQLQRY); } catch (Exception E) {
log.info E.getMessage()
E.printStackTrace();
}
return RS;
}
