/** * Class to encapsulate the excel data access layer of the framework * @author Cognizant */ public class ExcelDataAccess { private final String filePath;
private String datasheetName; /** * Function to get the Excel sheet name * @return The Excel sheet name */ public String getDatasheetName() { return datasheetName; } /** * Function to set the Excel sheet name * @param datasheetName The Excel sheet name */ public void setDatasheetName(String datasheetName) { this.datasheetName = datasheetName; }
/** * Constructor to initialize the excel data filepath and filename * @param filePath The absolute path where the excel data file is stored * @param fileName The name of the excel data file (without the extension). * Note that .xlsx files are not supported, only .xls files are supported */ public ExcelDataAccess(String filePath) { this.filePath = filePath; }
private void checkPreRequisites() { if (datasheetName == null) { System.out.println("ExcelDataAccess.datasheetName is not set!"); } }
public boolean fileExist() throws IOException { boolean flag = true; String absoluteFilePath = filePath; FileInputStream fileInputStream = null; try { fileInputStream = new FileInputStream(absoluteFilePath);
fileInputStream.close(); } catch (FileNotFoundException e) { e.printStackTrace(); System.out.println("The specified file \"" + absoluteFilePath + "\" does not exist!"); flag = false; } return flag; } private HSSFWorkbook openFileForReading() { String absoluteFilePath = filePath;
FileInputStream fileInputStream = null; try { fileInputStream = new FileInputStream(absoluteFilePath); } catch (FileNotFoundException e) { e.printStackTrace(); System.out.println("The specified file \"" + absoluteFilePath + "\" does not exist!"); }
HSSFWorkbook workbook = null; try { workbook = new HSSFWorkbook(fileInputStream); //fileInputStream.close(); } catch (IOException e) { e.printStackTrace(); System.out.println("Error while opening the specified Excel workbook \"" + absoluteFilePath + "\""); }
return workbook; }
private HSSFSheet getWorkSheet(HSSFWorkbook workbook) { HSSFSheet worksheet = workbook.getSheet(datasheetName); if (worksheet == null) { System.out.println("The specified sheet \"" + datasheetName + "\"" + "does not exist within the workbook"); }
return worksheet; }
/** * Function to search for a specified key within a column, and return the corresponding row number * @param key The value being searched for * @param columnNum The column number in which the key should be searched * @param startRowNum The row number from which the search should start * @return The row number in which the specified key is found (-1 if the key is not found) */ public int getRowNum(String key, int columnNum, int startRowNum) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); FormulaEvaluator formulaEvaluator = workbook.getCreationHelper().createFormulaEvaluator();
String currentValue; for (int currentRowNum = startRowNum; currentRowNum <= worksheet.getLastRowNum(); currentRowNum++) {
HSSFRow row = worksheet.getRow(currentRowNum); HSSFCell cell = row.getCell(columnNum); currentValue = getCellValueAsString(cell, formulaEvaluator);
if (currentValue.equals(key)) { return currentRowNum; } }
return -1; }
private String getCellValueAsString(HSSFCell cell, FormulaEvaluator formulaEvaluator) { if (cell == null || cell.getCellType() == HSSFCell.CELL_TYPE_BLANK) { return ""; } else { if (formulaEvaluator.evaluate(cell).getCellType() == HSSFCell.CELL_TYPE_ERROR) { System.out.println("Error in formula within this cell! " + "Error code: " + cell.getErrorCellValue()); }
DataFormatter dataFormatter = new DataFormatter(); return dataFormatter.formatCellValue(formulaEvaluator.evaluateInCell(cell)); } }
/** * Function to search for a specified key within a column, and return the corresponding row number * @param key The value being searched for * @param columnNum The column number in which the key should be searched * @return The row number in which the specified key is found (-1 if the key is not found) */ public int getRowNum(String key, int columnNum) { return getRowNum(key, columnNum, 0); }
/** * Function to get the last row number within the worksheet * @return The last row number within the worksheet */ public int getLastRowNum() { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook);
return worksheet.getLastRowNum(); }
/** * Function to search for a specified key within a column, and return the corresponding occurence count * @param key The value being searched for * @param columnNum The column number in which the key should be searched * @param startRowNum The row number from which the search should start * @return The occurence count of the specified key */ public int getRowCount(String key, int columnNum, int startRowNum) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); FormulaEvaluator formulaEvaluator = workbook.getCreationHelper().createFormulaEvaluator();
int rowCount = 0; boolean keyFound = false;
String currentValue; for (int currentRowNum = startRowNum; currentRowNum <= worksheet.getLastRowNum(); currentRowNum++) {
HSSFRow row = worksheet.getRow(currentRowNum); HSSFCell cell = row.getCell(columnNum); currentValue = getCellValueAsString(cell, formulaEvaluator);
if (currentValue.equals(key)) { rowCount++; keyFound = true; } else { if (keyFound) { break; // Assumption: Keys always appear contiguously } } }
return rowCount; }
/** * Function to search for a specified key within a column, and return the corresponding occurence count * @param key The value being searched for * @param columnNum The column number in which the key should be searched * @return The occurence count of the specified key */ public int getRowCount(String key, int columnNum) { return getRowCount(key, columnNum, 0); }
/** * Function to search for a specified key within a row, and return the corresponding column number * @param key The value being searched for * @param rowNum The row number in which the key should be searched * @return The column number in which the specified key is found (-1 if the key is not found) */ public int getColumnNum(String key, int rowNum) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); FormulaEvaluator formulaEvaluator = workbook.getCreationHelper().createFormulaEvaluator();
HSSFRow row = worksheet.getRow(rowNum); String currentValue; for (int currentColumnNum = 0; currentColumnNum < row.getLastCellNum(); currentColumnNum++) {
HSSFCell cell = row.getCell(currentColumnNum); currentValue = getCellValueAsString(cell, formulaEvaluator);
if (currentValue.equals(key)) { return currentColumnNum; } }
return -1; }
/** * Function to get the value in the cell identified by the specified row and column numbers * @param rowNum The row number of the cell * @param columnNum The column number of the cell * @return The value present in the cell */ public String getValue(int rowNum, int columnNum) {
checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); FormulaEvaluator formulaEvaluator = workbook.getCreationHelper().createFormulaEvaluator(); String cell_value = null;
HSSFRow row = worksheet.getRow(rowNum); try { HSSFCell cell = row.getCell(columnNum); cell_value = getCellValueAsString(cell, formulaEvaluator); } catch(Exception ex) { HSSFRow new_row = worksheet.createRow(rowNum); HSSFCell cell = new_row.createCell(columnNum); cell.setCellType(Cell.CELL_TYPE_STRING); cell.setCellValue(""); }
return cell_value;
}
/** * Function to get the value in the cell identified by the specified row number and column header * @param rowNum The row number of the cell * @param columnHeader The column header of the cell * @return The value present in the cell */ public String getValue(int rowNum, String columnHeader) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); FormulaEvaluator formulaEvaluator = workbook.getCreationHelper().createFormulaEvaluator();
HSSFRow row = worksheet.getRow(0); //0 because header is always in the first row int columnNum = -1; String currentValue = null; for (int currentColumnNum = 0; currentColumnNum < row.getLastCellNum(); currentColumnNum++) {
HSSFCell cell = row.getCell(currentColumnNum); currentValue = getCellValueAsString(cell, formulaEvaluator);
if (currentValue.equals(columnHeader)) { columnNum = currentColumnNum; break; } }
if (columnNum == -1) { System.out.println("The specified column header \"" + columnHeader + "\"" + "is not found in the sheet \"" + datasheetName + "\"!"); } else { row = worksheet.getRow(rowNum); HSSFCell cell = row.getCell(columnNum); currentValue = getCellValueAsString(cell, formulaEvaluator); } return currentValue; }
/* Method to get the Number of Data Sets and its corresponding name from Scenario Sheet */
public ArrayList<String> extractExcelContentByColumnIndex(int columnIndex, String fileName){ ArrayList<String> columndata = new ArrayList<String>(); try { File f = new File(fileName) FileInputStream ios = new FileInputStream(f); HSSFWorkbook workbook = new HSSFWorkbook(ios); HSSFSheet sheet = workbook.getSheetAt(0); Iterator<Row> rowIterator = sheet.iterator(); columndata = new ArrayList<>();
while (rowIterator.hasNext()) { Row row = rowIterator.next(); Iterator<Cell> cellIterator = row.cellIterator(); while (cellIterator.hasNext()) { Cell cell = cellIterator.next();
if(row.getRowNum() > 0){ //To filter column headings if(cell.getColumnIndex() == columnIndex){// To match column index switch (cell.getCellType()) { case Cell.CELL_TYPE_NUMERIC: columndata.add(cell.getNumericCellValue()+""); break; case Cell.CELL_TYPE_STRING: columndata.add(cell.getStringCellValue()); break; } } } } } ios.close(); //log.info("Data: "+columndata); } catch (Exception e) { //log.info "Exception: "+e; } return columndata; }
public List < String > getColumnName(String sheetName) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); List < String > colList = new ArrayList < String > (); HSSFRow row = worksheet.getRow(0); //Get first row //following is boilerplate from the java doc short minColIx = row.getFirstCellNum(); //get the first column index for a row short maxColIx = row.getLastCellNum(); //get the last column index for a row for (short colIx = minColIx; colIx < maxColIx; colIx++) { //loop from first to last index HSSFCell cell = row.getCell(colIx); //get the cell colList.add(cell.getStringCellValue()); //add the cell contents (name of column) and cell index to the map } return colList; }
public int rownum() { HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); return worksheet.getLastRowNum(); }
public String returnparticluarData(String ColumnName, int rowNum) { HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); int colNum = worksheet.getRow(0).getLastCellNum(); for (int i = 0; i < colNum; i++) { if (worksheet.getRow(0).getCell(i).getStringCellValue().equalsIgnoreCase(ColumnName)) { return (worksheet.getRow(rowNum).getCell(i).getStringCellValue());
} } return null; }
