public ArrayList < ExecutableTestCaseBean > executableTCLists(ResultSet RS,String projectDirectory)
{
ArrayList < ExecutableTestCaseBean > TCLists = new ArrayList < ExecutableTestCaseBean > (); try {
while (RS.next())
{
ExecutableTestCaseBean ExecutableTestCaseBeanOBJ = new ExecutableTestCaseBean();
ExecutableTestCaseBeanOBJ.setModule(RS.getString("Module"));
//ExecutableTestCaseBeanOBJ.setWebServiceTriggered(RS.getString("WebServiceTriggered"));
ExecutableTestCaseBeanOBJ.setTCName(RS.getString("TestCaseName"));
ExecutableTestCaseBeanOBJ.setTestDataSource(RS.getString("TestDataSource"));
ExecutableTestCaseBeanOBJ.setAssertionSource(RS.getString("AssertionSource"));
ExecutableTestCaseBeanOBJ.setDBFlag(RS.getString("DB_Connection"));
if(RS.getString("DataSet").equalsIgnoreCase("ALL"))
{
String scenrioPath = projectDirectory + "/Scenarios/" + RS.getString("TestCaseName") + ".xls"
ArrayList<String> arr_dataSets = extractExcelContentByColumnIndex(2,scenrioPath)
String[] dataSets = arr_dataSets.toArray(new String[arr_dataSets.size()]);
ExecutableTestCaseBeanOBJ.setDataSet(dataSets);
}
else
{
ExecutableTestCaseBeanOBJ.setDataSet((RS.getString("DataSet")).split(","));
}
ExecutableTestCaseBeanOBJ.setTestCaseType(RS.getString("TestCaseType"));
TCLists.add(ExecutableTestCaseBeanOBJ);
}
} catch (SQLException e) {
e.printStackTrace();
}
return TCLists;
}
public void setValue(int rowNum, int columnNum, String cellValue) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); HSSFRow row = worksheet.getRow(rowNum); try { HSSFCell cell = row.getCell(columnNum); cell.setCellValue(cellValue); } catch(Exception e) { HSSFRow new_row = worksheet.createRow(rowNum); HSSFCell cell = new_row.createCell(columnNum); cell.setCellType(Cell.CELL_TYPE_STRING); cell.setCellValue(cellValue); } }
public int columnIndex(String sheetName, String columnName) { checkPreRequisites();
HSSFWorkbook workbook = openFileForReading(); HSSFSheet worksheet = getWorkSheet(workbook); int col_index = 0; HSSFRow row = worksheet.getRow(0); //Get first row //following is boilerplate from the java doc short minColIx = row.getFirstCellNum(); //get the first column index for a row short maxColIx = row.getLastCellNum(); //get the last column index for a row for (short colIx = minColIx; colIx < maxColIx; colIx++) { //loop from first to last index HSSFCell cell = row.getCell(colIx); //get the cell if(cell.getStringCellValue().equalsIgnoreCase(columnName)) { col_index = colIx; } } return col_index; }
}
