public static String getXMlNodevalue(String project, String node)
{
String globalFile = project + "/GlobalSettings.xml"
String nodevalue = null;
try { // Setup the parser
DocumentBuilderFactory builderFactory = DocumentBuilderFactory.newInstance(); DocumentBuilder builder = builderFactory.newDocumentBuilder();
// Read the XML file File inputFile = new File(globalFile);
InputStream inputStream = new FileInputStream(inputFile);
// Parse the XML file
Document doc = builder.parse(inputStream);
// Create an XPath instance
XPath xPath = XPathFactory.newInstance().newXPath();
// Evaluate simple expressions
String expression = "//Variable/" + node;
nodevalue = xPath.evaluate(expression, doc);
return nodevalue;
} catch (FileNotFoundException e) {
e.printStackTrace();
} catch (XPathExpressionException e) {
e.printStackTrace();
} catch (SAXException e) {
e.printStackTrace();
} catch (IOException e) {
e.printStackTrace();
} catch (ParserConfigurationException e) {
e.printStackTrace();
} }
public static String getSettingsProperty(String project, String propKey) { Properties properties = new Properties(); String strValue = "";
try { properties.load(new FileInputStream(project + "/Global Settings.properties"));
strValue = properties.getProperty(propKey); } catch (FileNotFoundException e) { e.printStackTrace(); } catch (IOException e) { e.printStackTrace(); } return strValue; }
