try { File destDir = new File(updatedDes); FileUtils.forceMkdir(destDir); //log.info ("**********Source:"+source) ; //log.info ("**********Destination:"+updatedDes); File srcDir = new File(source); FileUtils.copyDirectory(srcDir, destDir); } catch (IOException e) { e.printStackTrace(); } } }
public void createReportMetaXML(String projectPath, String module, String htmlReportPath) throws Exception { String projectId = null; String version = null; String reportXMLpath = getSettingsProperty(projectPath, "reportXMLpath"); String appType = "SERVICE"; String jiraUpload = null; String contactEmail = null; String createNewCycle = null; String environment = null; String buildNumber = null; String cycleName = null; //projectPath = "C:\\Snapshotviews\\wngautomation\\GIT\\EMAGorilla"; //metaXMLPath = "\\\\visnpapp134\\jenkins\\Results\\EMAGorilla\\Report_XML\\";// To be hardcoded //htmlReportPath="\\\\\\\\visnpapp134\\\\jenkins\\\\Results\\\\EMAGorilla\\\\EMA_Gorilla_Sanity_Suite\\\\Run_06-17-2015_07-40-43-AM\\\\Summary.html"; Pattern p = Pattern.compile(".*\\\\" + module + "\\\\ *(.*) *");
Matcher m = p.matcher(htmlReportPath);
m.find();
String runId = m.group(1);
String metaXMLFilePath = reportXMLpath + runId + ".xml";
DocumentBuilderFactory dbf = DocumentBuilderFactory.newInstance(); DocumentBuilder db = dbf.newDocumentBuilder(); jiraUpload = getSettingsProperty(projectPath, "JiraUpload"); projectId = getSettingsProperty(projectPath, "JiraProject"); version = getSettingsProperty(projectPath, "JiraRelease"); htmlReportPath = htmlReportPath + "/Summary.html"
createNewCycle = getSettingsProperty(projectPath, "JiraNewCycle"); contactEmail = getSettingsProperty(projectPath, "JiraOwnerEID") + "@erac.com"; environment = getSettingsProperty(projectPath, "Environment"); buildNumber = "1.0"; cycleName = getSettingsProperty(projectPath, "JiraCycleName");
if ("true".equalsIgnoreCase(jiraUpload)) { createReportMetaXML(projectId, version, module, appType, metaXMLFilePath, htmlReportPath, contactEmail, createNewCycle, environment, buildNumber, cycleName); } }
public static void createReportMetaXML(String project, String version, String module, String appType, String metaXMLFilePath, String htmlReportPath, String jiraOwner, String createNewCycle, String environment, String buildNumber, String cycleName) throws Exception {
DocumentBuilderFactory docFactory = DocumentBuilderFactory.newInstance(); DocumentBuilder docBuilder = docFactory.newDocumentBuilder();
// root elements Document doc = docBuilder.newDocument(); Element rootElement = doc.createElement("report"); doc.appendChild(rootElement);
// firstname elements Element elProject = doc.createElement("project"); elProject.appendChild(doc.createTextNode(project)); rootElement.appendChild(elProject);
// lastname elements Element elVersion = doc.createElement("version"); elVersion.appendChild(doc.createTextNode(version)); rootElement.appendChild(elVersion);
// nickname elements Element elModule = doc.createElement("module"); elModule.appendChild(doc.createTextNode(module)); rootElement.appendChild(elModule);
// salary elements Element elAppType = doc.createElement("appType"); elAppType.appendChild(doc.createTextNode(appType)); rootElement.appendChild(elAppType);
Element elReportPath = doc.createElement("reportPath"); elReportPath.appendChild(doc.createTextNode(htmlReportPath)); rootElement.appendChild(elReportPath);
Element elJiraOwner = doc.createElement("jiraOwner"); htmlReportPath elJiraOwner.appendChild(doc.createTextNode(jiraOwner)); rootElement.appendChild(elJiraOwner);
Element elcreateNewCycle = doc.createElement("createNewCycle"); elcreateNewCycle.appendChild(doc.createTextNode(createNewCycle)); rootElement.appendChild(elcreateNewCycle);
Element elenvironment = doc.createElement("environment"); elenvironment.appendChild(doc.createTextNode(environment)); rootElement.appendChild(elenvironment);
Element elbuildNumber = doc.createElement("buildNumber"); elbuildNumber.appendChild(doc.createTextNode(buildNumber)); rootElement.appendChild(elbuildNumber);
Element elcycleName = doc.createElement("cycleName"); elcycleName.appendChild(doc.createTextNode(cycleName)); rootElement.appendChild(elcycleName);
TransformerFactory transformerFactory = TransformerFactory.newInstance(); Transformer transformer = transformerFactory.newTransformer(); transformer.setOutputProperty(OutputKeys.ENCODING, "UTF-8"); transformer.setOutputProperty(OutputKeys.INDENT, "yes"); DOMSource source = new DOMSource(doc); StreamResult result = new StreamResult(new File(metaXMLFilePath)); transformer.transform(source, result); }
